# puzzle06a.py
import string

def main():
    input_buffer = ""
    MARKER_LEN = 4
    char_count = dict()

    for char in range((MARKER_LEN-1), (len(input_buffer))):
        marker_test = input_buffer[(char-MARKER_LEN):char]
        char_set = set(marker_test)
        for item in char_set:
            char_count[item] = marker_test.count(item)
        if list(set(char_count.values())) == [1]:
            break

    print("Characters before first start-of-packet marker: {n}\n".format(n=char))
    # Answer: 1909

if __name__ == "__main__":
    main()
